mapper.select:
select * from sardine_bintype t
where
t.code=#{code,jdbcType=VARCHAR}

select t.* from sardine_bintype t where 1=1
and t.code like CONCAT(CONCAT('%', #{code}), '%')
and t.name like CONCAT(CONCAT('%', #{name}), '%')
order by t.code ${orderDir}
order by t.name ${orderDir}

mapper.delete:
delete from sardine_bintype
where uuid =
#{uuid,jdbcType=VARCHAR}
and version=
#{version,jdbcType=LONGVARCHAR}
