mapper.select:
select u.*
from sardine_supplier s
where s.uuid = #{uuid,jdbcType=VARCHAR}

select s.*
from sardine_supplier s
where 1=1

	and s.code like CONCAT(CONCAT('%', #{code}),
	'%')


	and s.name like CONCAT(CONCAT('%', #{name}),
	'%')


	and s.state = #{state,jdbcType=VARCHAR}


	order by t.code ${orderDir}


	order by t.name ${orderDir}
