mapper.select:
select t.*, s.code suppliercode, s.name suppliername, w.code wrhcode, w.name wrhname
         from db_proxy.sardine_OrderBill t,
              db_proxy.sardine_supplier s,
              db_proxy.sardine_wrh w
where t.wrhuuid = w.uuid and t.supplieruuid = s.uuid 
  and t.uuid = #{uuid, jdbcType=VARCHAR}

select t.*, s.code suppliercode, s.name suppliername, w.code wrhcode, w.name wrhname 
         from db_proxy.sardine_OrderBill t,
              db_proxy.sardine_supplier s,
              db_proxy.sardine_wrh w
where t.wrhuuid = w.uuid and t.supplieruuid = s.uuid 
  and t.billnumber = #{billNumber, jdbcType=VARCHAR}
  and t.companyUuid = #{companyUuid, jdbcType=VARCHAR}

mapper.delete:
delete from db_proxy.sardine_orderbill 
where uuid = #{uuid, jdbcType=VARCHAR}
  and version = #{version, jdbcType=LONGVARCHAR}

mapper.update:
update db_proxy.sardine_OrderBillItem t
set t.receivedQty = #{qty, jdbcType=DECIMAL},
    t.receivedCaseQtyStr = #{caseQtyStr, jdbcType=VARCHAR}
where t.uuid = #{uuid, jdbcType=VARCHAR}

mapper.select:
select t.*, a.code articlecode, a.name articlename
       from db_proxy.sardine_OrderBillItem t,
            db_proxy.sardine_article a
      where t.articleuuid = a.uuid 
        and t.orderBillUuid = #{orderBillUuid, jdbcType=VARCHAR}

select t.*, s.code suppliercode, s.name suppliername, w.code wrhcode, w.name wrhname from 
   db_proxy.sardine_OrderBill t,
   db_proxy.sardine_wrh w,
   db_proxy.sardine_supplier s
where t.wrhuuid = w.uuid and t.supplieruuid = s.uuid 
  and t.companyuuid = #{companyUuid, jdbcType=VARCHAR}

	and s.code = #{supplierCode, jdbcType=VARCHAR}


	and t.supplierUuid = #{supplierUuid, jdbcType=VARCHAR}


	and t.wrhuuid = #{wrhUuid, jdbcType=VARCHAR}


	and w.code = #{wrhCode, jdbcType=VARCHAR}


	and t.state = #{state, jdbcType=VARCHAR}


	and t.billNumber = #{billNumber, jdbcType=VARCHAR}


	and exists (select 1 from db_proxy.sardine_OrderBillitem oi
	oi.articleUuid = #{articleUuid, jdbcType=VARCHAR} and t.uuid = oi.orderbilluuid)
