mapper.delete:
delete from
db_proxy.sardine_acceptanceBill
where uuid = #{uuid, jdbcType=VARCHAR}
and
version = #{version, jdbcType=LONGVARCHAR}

mapper.select:
select t.*, w.code wrhcode,
w.name wrhname from
db_proxy.sardine_acceptanceBill t,
db_proxy.sardine_wrh w
where
t.wrhuuid = w.uuid
and t.companyuuid = #{companyUuid,
jdbcType=VARCHAR}

	and t.customerCode = #{customerCode, jdbcType=VARCHAR}


	and t.wrhCode = #{wrhCode, jdbcType=VARCHAR}


	and t.state = #{state, jdbcType=VARCHAR}


	and t.deliverySystem = #{deliverySystem, jdbcType=VARCHAR}


	and t.deliveryType = #{deliveryType, jdbcType=VARCHAR}


	and t.billNumber = #{billNumber, jdbcType=VARCHAR}
