mapper.select:
select
t.*, c.code customerCode, c.name customerName from db_proxy.sardine_pickupbill t, 
db_proxy.sardine_customer c
where t.customeruuid = c.uuid and t.billNumber = #{billNumber,jdbcType=VARCHAR} 
and t.companyUuid = #{companyUuid, jdbcType=VARCHAR}

select t.*
from db_proxy.sardine_pickupbill t, 
     db_proxy.sardine_customer c
where t.customeruuid = c.uuid 
  and t.companyUuid = #{companyUuid, jdbcType=VARCHAR}

	and t.billNumber like CONCAT(CONCAT('%',
	#{billNumberLike}), '%')


	and t.sourcebillnumber like CONCAT(CONCAT('%',
	#{waveBillNumberLike}), '%') 
	and t.sourcebilltype = '波次单'


	and t.type = #{pickTypeEquals}


	and t.customeruuid = #{customerUuidEquals}


	and t.pickeruuid = #{pickerUuidEquals}


	and t.deliverytype = #{deliveryTypeEquals}


	and t.method = #{pickMethodEquals}

mapper.delete:
delete from db_proxy.sardine_pickupbill t
where t.uuid = #{uuid,jdbcType=VARCHAR} 
 and  t.version = #{version,jdbcType=LONGVARCHAR}
