mapper.select:
select t.*,w.code wrhCode,w.name wrhName from
db_proxy.sardine_returnntcbill t,db_proxy.sardine_wrh w where
t.wrhuuid=w.uuid and t.uuid=#{uuid,jdbcType=VARCHAR}

select t.*,w.code wrhCode,w.name wrhName from
db_proxy.sardine_returnntcbill t,db_proxy.sardine_wrh w where
t.wrhuuid=w.uuid and t.billNumber=#{billNumber,jdbcType=VARCHAR}
and
t.companyUuid=#{companyUuid,jdbcType=VARCHAR}

select t.*, w.code wrhCode, w.name wrhName from db_proxy.sardine_returnNtcBill t, db_proxy.sardine_wrh w where t.wrhuuid =w.uuid
and t.billNumber like CONCAT(CONCAT('%', #{billNumber,jdbcType=VARCHAR}), '%')
and t.state = #{state,jdbcType=VARCHAR}
and t.customerCode like CONCAT(CONCAT('%', #{customerCode,jdbcType=VARCHAR}), '%')
and t.customerName like CONCAT(CONCAT('%', #{customerName,jdbcType=VARCHAR}), '%')
and t.sourceBillNumber like CONCAT(CONCAT('%', #{sourceBillNumber,jdbcType=VARCHAR}), '%')
and t.returnDate <= #{returnDateLessThan,jdbcType=TIMESTAMP}
and t.returnDate >= #{returnDateMoreThan,jdbcType=TIMESTAMP}
and EXISTS (select 1 from db_proxy.sardine_article a,db_proxy.sardine_returnntcbillitem i where a.uuid=i.articleuuid and a.code= #{articleCode,jdbcType=VARCHAR})
and EXISTS (select 1 from db_proxy.sardine_article a,db_proxy.sardine_returnntcbillitem i where a.uuid=i.articleuuid and a.name= #{articleName,jdbcType=VARCHAR})
and w.code=#{wrhCode,jdbcType=VARCHAR}
and EXISTS (select 1 from db_proxy.sardine_supplier s,db_proxy.sardine_returnntcbillitem i where s.uuid=i.supplieruuid and s.name= #{supplierName,jdbcType=VARCHAR})
and EXISTS (select 1 from db_proxy.sardine_supplier s,db_proxy.sardine_returnntcbillitem i where s.uuid=i.supplieruuid and s.code= #{supplierCode,jdbcType=VARCHAR})
and t.companyuuid=#{companyUuid,jdbcType=VARCHAR}
order by t.billNumber ${orderDir}
order by t.state ${orderDir}
order by t.customerCode ${orderDir}
