mapper.select:
select t.* from
db_proxy.sardine_returnSupplierBill t where
t.uuid=#{uuid,jdbcType=VARCHAR}

select t.* from
db_proxy.sardine_returnSupplierBill t where
t.billNumber=#{billNumber,jdbcType=VARCHAR}
and
t.companyUuid=#{companyUuid,jdbcType=VARCHAR}

select t.* from db_proxy.sardine_returnSupplierBill t where 1=1
and t.companyuuid=#{companyUuid,jdbcType=VARCHAR}
and t.billNumber like CONCAT(CONCAT('%', #{billNumber,jdbcType=VARCHAR}), '%')
and t.state = #{state,jdbcType=VARCHAR}
and t.rtnSupplierNtcBillNumber like CONCAT(CONCAT('%', #{rtnSupplierNtcBillNumber,jdbcType=VARCHAR}), '%')
and t.supplierCode like CONCAT(CONCAT('%', #{supplierCode,jdbcType=VARCHAR}), '%')
and t.supplierName like CONCAT(CONCAT('%', #{supplierName,jdbcType=VARCHAR}), '%')
and t.wrhUuid = #{wrhUuid,jdbcType=VARCHAR}
and t.operateMethod = #{operateMethod,jdbcType=VARCHAR}
and t.returnerCode like CONCAT(CONCAT('%', #{returnerCode,jdbcType=VARCHAR}), '%')
and t.returnerName like CONCAT(CONCAT('%', #{returnerName,jdbcType=VARCHAR}), '%')
and EXISTS (select 1 from db_proxy.sardine_returnSupplierBillItem i and i.articleCode like CONCAT(CONCAT('%', #{customerName,jdbcType=VARCHAR}), '%') )
and EXISTS (select 1 from db_proxy.sardine_returnSupplierBillItem i and i.binCode like CONCAT(CONCAT('%', #{binCode,jdbcType=VARCHAR}), '%') )
and EXISTS (select 1 from db_proxy.sardine_returnSupplierBillItem i and i.containerBarcode like CONCAT(CONCAT('%', #{containerBarcode,jdbcType=VARCHAR}), '%') )
and t.returnSupplierDate < #{returnSupplierDateLessThanOrEqual,jdbcType=TIMESTAMP}
and t.returnSupplierDate >= #{returnSupplierDateGreaterThanOrEqual,jdbcType=TIMESTAMP}
order by t.billNumber ${orderDir}
order by t.state ${orderDir}
order by t.rtnSupplierNtcBillNumber ${orderDir}
order by t.supplierCode ${supplier}

select t.* from db_proxy.sardine_returnSupplierBillItem t
where
t.returnSupplierBillUuid=#{uuid,jdbcType=VARCHAR}
