mapper.select:
select t.* from
db_proxy.sardine_shipbill t where
t.uuid=#{uuid,jdbcType=VARCHAR}

select t.* from
db_proxy.sardine_shipbill t where
t.billNumber=#{billNumber,jdbcType=VARCHAR}
and
t.companyUuid=#{companyUuid,jdbcType=VARCHAR}

select t.* from db_proxy.sardine_shipbill t where 1=1
and t.companyuuid=#{companyUuid,jdbcType=VARCHAR}
and t.billNumber like CONCAT(CONCAT('%', #{billNumber,jdbcType=VARCHAR}), '%')
and t.state = #{state,jdbcType=VARCHAR}
and t.vehicleNum like CONCAT(CONCAT('%', #{vehicleNum,jdbcType=VARCHAR}), '%')
and EXISTS (select 1 from db_proxy.sardine_shipbillcustomeritem i and i.customerCode like CONCAT(CONCAT('%', #{customerCode,jdbcType=VARCHAR}), '%') )
and EXISTS (select 1 from db_proxy.sardine_shipbillcustomeritem i and i.customerName like CONCAT(CONCAT('%', #{customerName,jdbcType=VARCHAR}), '%') )
and t.deliveryType = #{deliveryType,jdbcType=VARCHAR}
and t.operateMethod = #{operateMethod,jdbcType=VARCHAR}
and t.driverCode like CONCAT(CONCAT('%', #{driverCode,jdbcType=VARCHAR}), '%')
and t.driverName like CONCAT(CONCAT('%', #{driverName,jdbcType=VARCHAR}), '%')
and EXISTS (select 1 from db_proxy.sardine_shipbillcontainerstock i and i.articleCode like CONCAT(CONCAT('%', #{customerName,jdbcType=VARCHAR}), '%') and i.SHIPBILLUUID=t.uuid )
and EXISTS (select 1 from db_proxy.sardine_shipbillcontainerstock i where i.shipperCode like CONCAT(CONCAT('%', #{shipperCode,jdbcType=VARCHAR}), '%') and i.SHIPBILLUUID=t.uuid )
and EXISTS (select 1 from db_proxy.sardine_shipbillcontainerstock i where i.shipperName like CONCAT(CONCAT('%', #{shipperName,jdbcType=VARCHAR}), '%') )
and EXISTS (select 1 from db_proxy.sardine_shipbillcontainerstock i and i.containerBarcode like CONCAT(CONCAT('%', #{containerBarcode,jdbcType=VARCHAR}), '%') )
order by t.billNumber ${orderDir}
order by t.state ${orderDir}
order by t.vehicleNum ${orderDir}

select t.* from db_proxy.sardine_shipbillcustomeritem t
where
t.shipBillUuid=#{uuid,jdbcType=VARCHAR}

select t.* from db_proxy.sardine_shipbillcontainerstock t
where
t.shipBillUuid=#{uuid,jdbcType=VARCHAR}

mapper.delete:
delete from
db_proxy.sardine_shipbill where uuid = #{uuid, jdbcType=VARCHAR}
and
version = #{version, jdbcType=LONGVARCHAR}

delete from
db_proxy.sardine_shipbillcustomeritem where shipbilluuid =
#{shipBillUuid, jdbcType=VARCHAR}

delete from
db_proxy.sardine_shipbillcontainerstock where shipbilluuid =
#{shipBillUuid, jdbcType=VARCHAR}

mapper.select:
select s.*, c.UUID customeruuid, c.code customercode, c.name customername,i.line ,p.deliveryType,p.deliverySystem, s.billuuid sourceBillUuid, s.billtype sourceBillType, s.billnumber sourceBillNumber from db_proxy.sardine_stock s,db_proxy.sardine_shipbillcustomeritem i, db_proxy.sardine_customer c,db_proxy.sardine_shipbillcontainerstock p, db_proxy.sardine_bin b where c.uuid = i.customeruuid and p.customeruuid = c.uuid and s.OWNER = c.uuid and s.BINCODE = b.code and s.companyuuid = b.companyuuid and b.binusage = 'CollectBin' and s.STATE = 'normal' and s.companyuuid = #{companyUuid, jdbcType=VARCHAR}
and s.articleCode like CONCAT('%',#{articleCodeLike},'%')
and s.bincode like CONCAT('%',#{binCodeLike},'%')
and s.containerbarcode like CONCAT('%',#{containerBarcodeLike},'%')
and c.code like CONCAT('%',#{customerCodeLike},'%')
and s.suppliercode like CONCAT('%',#{supplierCodeLike},'%')
and i.line = #{line,jdbcType=VARCHAR}
and p.deliveryType = #{deliveryType,jdbcType=VARCHAR}
and p.deliverySystem = #{deliverySystem,jdbcType=VARCHAR}
